<script>

const btn = document.querySelector('.btn-header-one')
const popup = document.querySelector('.popup')
const shdow = document.querySelector('.popup-shadow')
const btnCloser = document.querySelector('.popup-closer')
const body = document.querySelector('body')

btn.addEventListener('click', function() {
  popup.style.display = 'block';
  shdow.style.display = 'block';
  body.style.overflow = 'hidden';
});

btnCloser.addEventListener('click', function() {
  popup.style.display = 'none';
  shdow.style.display = 'none';
  body.style.overflow = 'visible';
});

  
</script>


